let fs = require('fs');
let pureImage = require('../src/pureimage');
let image = pureImage.make(10,10);
let c = image.getContext('2d');
c.strokeStyle = 'rgba(255,255,255,0.5)';
c.lineWidth = 5;
c.beginPath();
c.moveTo(0,0)
c.lineTo(5,5)
c.stroke();
c.closePath();
pureImage.encodePNGToStream(image, fs.createWriteStream(__dirname + '/out2.png'))
